sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    gs.addInfoMessage('BR Async Started');
	var componentRecord = new GlideRecord('x_1107936_cconnect_components_schemas');
    componentRecord.addQuery('table_sys_id', current.sys_id);
    componentRecord.orderBy('schema_name');
    componentRecord.query();
    var added = new Map();
    var obj = new global.IntegrationMap();
    while (componentRecord.next()) {
        if (!(added.has(componentRecord.schema_name.trim().toLowerCase().slice(0, componentRecord.schema_name.trim().toLowerCase().length - 1)) && componentRecord.schema_name.trim().toLowerCase().slice(-1) == 's')) {
            added.set(componentRecord.schema_name.trim().toLowerCase(), 'True');
            var tableName = componentRecord.schema_name + "_" + current.sys_id;
            var tableSysId = obj.createTable(tableName, componentRecord.schema_name, true, true, true, gs.getCurrentScopeName());
            var schemaData = componentRecord.properties.trim().split('\n');
            for (var a in schemaData) {
                obj.addFieldToTable(tableSysId, tableName, schemaData[a].split(':')[0].toLowerCase(), schemaData[a].split(':')[0], schemaData[a].split(':')[1].trim().toLowerCase());
            }
        } else {
            added.set(componentRecord.schema_name.trim().toLowerCase(), 'False');
        }
    }
})(current, previous);
