sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var dropdownValueAuth = g_form.getValue('authentication');

    // Perform actions based on the new value
    if (dropdownValueAuth == 'Basic') {
        g_form.setVisible('userid', true);
        g_form.setVisible('password', true);
        g_form.setVisible('client_id', false);
        g_form.setVisible('client_secret', false);
        g_form.setVisible('access_token_url', false);
        g_form.setVisible('scope', false);
        g_form.setVisible('grant_type', false);
        g_form.setValue('another_field', 'value1');
    } else if (dropdownValueAuth == 'OAuth2.0') {
        g_form.setVisible('userid', true);
        g_form.setVisible('password', true);
        g_form.setVisible('client_id', true);
        g_form.setVisible('client_secret', true);
        g_form.setVisible('access_token_url', true);
        g_form.setVisible('scope', true);
        g_form.setVisible('grant_type', true);
    }

}
